update sales 
set NewPID = CONVERT(int,RIGHT(convert(varchar(max),SPID),2)) ;


select * from people;
alter table people
add NewSPIDofPeopleTable int; 
update people 
set NewSPIDofPeopleTable =convert(int,RIGHT(CONVERT(varchar(max),SPID),2)) ;


select * from people;
select * from sales;


select P.Salesperson, SUM(S.Amount) AS SalesAmount
from people as P
right join sales as S
on  P.[NewSPIDofPeopleTable] = S.NewSPID
where YEAR(S.SaleDate) = 2022 and MONTH(S.SaleDate) = 01
group by P.Salesperson;


--Which product sells more boxes ?

ALTER TABLE products
ADD NewPID int;
update products 
set NewPID = CONVERT(int,RIGHT(convert(varchar(max),PID),2)) ;


select * from products;
select * from sales;

select SUM(S.Boxes) AS BoxesQuantity,P.Product 
from sales AS S
inner join products AS P
on S.NewPID = P.NewPID
Group by P.product;

